import jxl.*

import jxl.write.*

import net.sf.json.groovy.*

import groovy.json.JsonSlurper

import com.eviware.soapui.support.GroovyUtils

import groovy.json.JsonBuilder


File ExecutionFile = new File("C:\\Saurabh\\users5.xls")

Workbook wk = Workbook.getWorkbook(ExecutionFile);

WritableWorkbook workbook1 = Workbook.createWorkbook(ExecutionFile,wk)

WritableSheet sheet= workbook1.getSheet(0);

log.info sheet

def response = context.expand('${JDBCRequestforDatabaset#Response}').toString()

log.info response

def json = new JsonSlurper().parseText(response)

def groovyutils = new com.eviware.soapui.support.GroovyUtils(context)

def DBholder = groovyUtils.getXmlHolder( "JDBC RequestForDatabase #ResponseAsXml" )

def xmlUtil = new com.eviware.soapui.support.xml.XmlUtils();

def nodes = json.getDomNodes( "//*" );

def DBnodes = DBholder.getDomNodes( "//*" );



// create list of XML strings

def list = []
def XMLlist = []
def DBXMLlist = []
for( node in nodes ){

   def value = com.eviware.soapui.support.xml.XmlUtils.getNodeValue( node )
   if((value!=null))
    {
	 if(!(value.isAllWhitespace()))
		{

			XMLlist.add(value)

		}

    }
}

def Slist = XMLlist.size()

log.info "XML_List =" + XMLlist




for( node in DBnodes )
{
	def DBvalue = com.eviware.soapui.support.xml.XmlUtils.getNodeValue( node )
	DBXMLlist.add(DBvalue)  
}

log.info "XML_List =" + DBXMLlist

def DBSlist = DBXMLlist.size()

def DBAXMLlist =[]
int b =0;



for (int p=0;p<=b;p++)
{
    while(b<=DBSlist)
	{
		if((DBXMLlist[b]!=null))
		{
			if(!(DBXMLlist[b].isAllWhitespace()))
			{
				DBAXMLlist.add(DBXMLlist[b].trim())
			}
		}
	}

	b=b+1

}

/*}  */

log.info (DBSlist)

int flag =0

int row=1

int col=1





for (int i=0;i<DBSlist;i++)
{
	def cnt=XMLlist[j].equals(DBAXMLlist[i])
	log.info(cnt)
	int ROW1=row.toInteger()
	int COL1=col.toInteger()

	if(!cnt)
	{
		Label label1 = new Label(ROW1, COL1, "There is a mismatch found at Tag No")
		flag=1
		sheet.addCell(label1);
		row=row+1
		col1=col+1
		ROW1=row.toInteger()
		COL1=col.toInteger()
		def tag=j.toString()
		Label label2 = new Label(ROW1, COL1, tag)
		sheet.addCell(label2);   
		row=row+1
		def ROW=row.toString()
		testRunner.testCase.testSuite.project.setPropertyValue("ROW",ROW)
	}

	j=j+1
}

/*}*/



	if(!flag)
	{
		Label label3 = new Label(1, 1, "All matched")
		sheet.addCell(label3)     
	}

Label label4 = new Label(3, 1, json)

sheet.addCell(label4)

def Value=j.toString()

testRunner.testCase.testSuite.project.setPropertyValue("Value",Value)

workbook1.write()

workbook1.close()

